from django import template

register = template.Library()


@register.simple_tag
def create_button(type,btn_class,text,name=None,onclick=None):
    attrs = f'''type="{type}" class="{btn_class}"'''
    if name: attrs += f''' name="{name}"'''
    if onclick: attrs += f''' onclick="location.href='{onclick}'"'''
    button = f'''<button {attrs}>{text}</button>'''
    
    return button